import axios from 'axios'
import { CREATE_URL_REQUEST, CREATE_URL_SUCCESS, CREATE_URL_FAIL } from '../action';


export const createUrl = (url) => {
    return async dispatch => {
        try {
            dispatch({
                type: CREATE_URL_REQUEST
            })
            const config = {
                headers: { 'Content-Type': 'application/json' }
            }
            const { data } = await axios.post("https://urlshortner-backend-vj.herokuapp.com/api/url",
                (url),
                config)
            dispatch({
                type: CREATE_URL_SUCCESS,
                payload: data
            })
        }
        catch (error) {
            dispatch({
                type: CREATE_URL_FAIL,
                payload: error.response && error.response.data.message
            })

        }
    }
}
